<?phpfunction DateIndo($tgl) {	if ($tgl) {		setlocale (LC_TIME, 'id_ID');		$date = strftime( "%d-%m-%Y", $tgl);		return $date;	}}function get_date() {	return strtotime(date('Y-m-d H:i:s'));}function set_date_normal($date) {	return date('Y-m-d H:i:s', $date);}function DateTmInd($tgl){	if ($tgl) {		$tgl = set_date_normal($tgl);		$tanggal = substr($tgl,8,2);		$bulan = getBulan(substr($tgl,5,2));		$tahun = substr($tgl,0,4);		$jam = substr($tgl,10,3);		$menit = substr($tgl,13,3);		return $tanggal.' '.$bulan.' '.$tahun.' '.$jam.''.$menit ;		 	}}function DateTmIndShort($tgl){	if ($tgl) {		$tgl = set_date_normal($tgl);		$tanggal = substr($tgl,8,2);		$bulan = getBulan(substr($tgl,5,2));		$tahun = substr($tgl,0,4);		return $tanggal.' '.$bulan.' '.$tahun.'';		 	}}function get_hari($date) {	$array_hari = array(1=>'Senin','Selasa','Rabu','Kamis','Jumat', 'Sabtu','Minggu');	$hari = $array_hari[date('N', $date)];	return $hari;}function getBulan($bln){	switch ($bln){		case 1: 		return "Januari";		break;		case 2:		return "Februari";		break;		case 3:		return "Maret";		break;		case 4:		return "April";		break;		case 5:		return "Mei";		break;		case 6:		return "Juni";		break;		case 7:		return "Juli";		break;		case 8:		return "Agustus";		break;		case 9:		return "September";		break;		case 10:		return "Oktober";		break;		case 11:		return "November";		break;		case 12:		return "Desember";		break;	}}